svg.script:
        
        function load() {
            var gradient = document.getElementById('myLinearGradient1');
            var colors = document.URL.split('@');
            colors[0] = colors[0].match(/[?#](.*)/)[1];

            for (var idx in colors) {
                colors[idx] = colors[idx].split('|');
                var stop = document.createElementNS('http://www.w3.org/2000/svg', 'stop');
                stop.setAttribute('stop-color', decodeURIComponent(colors[idx][0]));
                stop.setAttribute('offset', colors[idx][1]);
                gradient.appendChild(stop);
            }
        }
